contracts/StakingManager.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.21;

import {IStakingV2} from "./interfaces/IStakingV2.sol";
import {IStakingManager} from "./interfaces/IStakingManager.sol";
import {IAlphaToken} from "./interfaces/IAlphaToken.sol";
import {IAlphaTokenFactory} from "./interfaces/IAlphaTokenFactory.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title StakingManager
 * @author kitanovicd (TaoFi)
 * @notice This contract manages staking operations by acting as a proxy to a staking precompile.
 * It allows users to stake TAO tokens and receive corresponding AlphaTokens, which represent their
 * staked position in a specific subnet (netuid).
 * @dev This contract is owned and managed by a single owner who can set administrative addresses.
 * It relies on external interfaces for the staking precompile, alpha token, and its factory.
 */
contract StakingManager is IStakingManager, OwnableUpgradeable, ReentrancyGuard {
    uint256 public constant MAX_FEE = 100_00; // 100%

    /// @notice The conversion ratio from TAO (in wei) to AlphaToken representation. 1 TAO = 10^9 Alpha.
    uint256 public constant RATIO_TAO_TO_ALPHA = 1e9;

    /// @notice The address of the Subtensor staking precompile contract. This is immutable.
    address public stakingPrecompile;

    /// @notice The address of the factory contract used to deploy new AlphaToken contracts.
    address public alphaTokenFactory;

    /// @notice The public key associated with this staking manager's coldkey on the Subtensor network.
    bytes32 public pubKey;

    /// @notice The fee for staking.
    uint256 public stakingFee;

    /// @notice The fee for unstaking.
    uint256 public unstakingFee;

    /// @notice The minimum transaction size for staking.
    uint256 public minTxSize;

    /// @notice Maps a network UID (netuid) and hotkey to its corresponding AlphaToken contract address.
    mapping(uint256 netuid => mapping(bytes32 hotkey => address alphaToken)) public alphaTokens;

    // --- Errors ---
    error AddStakeFailed();
    error RemoveStakeFailed();
    error TransferStakeFailed();
    error InsufficientAmountOut();
    error EtherTransferFailed();
    error InvalidAmount();
    error InsufficientTaoReceived(uint256 received, uint256 minExpected);
    error InvalidFee();
    error TxTooSmall();

    // --- Events ---
    event StakeAdded(uint256 indexed netuid, bytes32 indexed hotkey, uint256 alphaReceived, uint256 sharesToMint);
    event StakeRemoved(uint256 indexed netuid, bytes32 indexed hotkey, uint256 alphaToUnstake, uint256 amount);
    event Staked(
        address indexed user,
        uint256 indexed netuid,
        bytes32 hotkey,
        uint256 taoAmount,
        uint256 feeAmount,
        uint256 alphaAmount
    );
    event Unstaked(
        address indexed user,
        uint256 indexed netuid,
        bytes32 hotkey,
        uint256 alphaAmount,
        uint256 taoAmount,
        uint256 feeAmount
    );
    event PubKeySet(bytes32 newPubKey);
    event FeesSet(uint256 stakingFee, uint256 unstakingFee);
    event StakingPrecompileSet(address indexed newStakingPrecompile);
    event AlphaTokenFactorySet(address indexed newFactory);
    event AlphaTokenDeployed(uint256 indexed netuid, address indexed tokenAddress);
    event FeesClaimed(address indexed receiver, uint256 amount);
    event MinTxSizeSet(uint256 minTxSize);

    /**
     * @dev Allows the contract to receive Ether, primarily from the unstaking process.
     */
    receive() external payable {}

    constructor() {
        _disableInitializers();
    }

    /**
     * @notice Sets up the StakingManager contract.
     * @param _stakingPrecompile The address of the staking precompile.
     * @param _alphaTokenFactory The initial address of the AlphaToken factory.
     */
    function initialize(address _stakingPrecompile, address _alphaTokenFactory) public initializer {
        require(_stakingPrecompile != address(0), "Staking precompile cannot be zero address");
        require(_alphaTokenFactory != address(0), "Factory cannot be zero address");

        __Ownable_init();
        stakingPrecompile = _stakingPrecompile;
        alphaTokenFactory = _alphaTokenFactory;
    }

    /**
     * @notice Claims accrued fees from the staking manager.
     * @dev Only callable by the contract owner. Emits a {FeesClaimed} event.
     * @param receiver The address to send the fees to.
     */
    function claimAccruedFees(address receiver) external onlyOwner {
        uint256 amount = address(this).balance;
        payable(receiver).transfer(amount);
        emit FeesClaimed(receiver, amount);
    }

    /**
     * @notice Sets the public key for staking operations.
     * @dev Only callable by the contract owner. Emits a {PubKeySet} event.
     * @param _pubKey The new public key to use.
     */
    function setPubKey(bytes32 _pubKey) external onlyOwner {
        pubKey = _pubKey;
        emit PubKeySet(_pubKey);
    }

    /**
     * @notice Sets the fees for staking and unstaking.
     * @dev Only callable by the contract owner. Emits a {FeesSet} event.
     * @param _stakingFee The new staking fee.
     * @param _unstakingFee The new unstaking fee.
     */
    function setFees(uint256 _stakingFee, uint256 _unstakingFee) external onlyOwner {
        if (stakingFee > MAX_FEE) revert InvalidFee();
        if (unstakingFee > MAX_FEE) revert InvalidFee();

        stakingFee = _stakingFee;
        unstakingFee = _unstakingFee;

        emit FeesSet(_stakingFee, _unstakingFee);
    }

    /**
     * @notice Updates the address of the AlphaToken factory.
     * @dev Only callable by the contract owner. Emits an {AlphaTokenFactorySet} event.
     * @param _alphaTokenFactory The new factory address.
     */
    function setAlphaTokenFactory(address _alphaTokenFactory) external onlyOwner {
        require(_alphaTokenFactory != address(0), "Factory cannot be zero address");
        alphaTokenFactory = _alphaTokenFactory;
        emit AlphaTokenFactorySet(_alphaTokenFactory);
    }

    /**
     * @notice Sets the address of the staking precompile.
     * @dev Only callable by the contract owner. Emits an {StakingPrecompileSet} event.
     * @param _stakingPrecompile The new staking precompile address.
     */
    function setStakingPrecompile(address _stakingPrecompile) external onlyOwner {
        require(_stakingPrecompile != address(0), "Staking precompile cannot be zero address");
        stakingPrecompile = _stakingPrecompile;
        emit StakingPrecompileSet(_stakingPrecompile);
    }

    /**
     * @notice Sets the minimum transaction size for staking.
     * @dev Only callable by the contract owner. Emits an {MinTxSizeSet} event.
     * @param _minTxSize The new minimum transaction size.
     */
    function setMinTxSize(uint256 _minTxSize) external onlyOwner {
        minTxSize = _minTxSize;
        emit MinTxSizeSet(_minTxSize);
    }

    /**
     * @notice Stakes TAO for a user and mints AlphaTokens in return.
     * @dev The amount of TAO to stake is determined by `msg.value`. The function will revert
     * if the amount sent is not a multiple of the ratio denominator.
     * @param hotkey The hotkey of the validator/miner to stake to.
     * @param netuid The network UID of the subnet.
     * @param receiver The address that will receive the minted AlphaTokens.
     * @param minAlphaToReceive The minimum amount of AlphaTokens the caller is willing to accept.
     */
    function stake(bytes32 hotkey, uint256 netuid, address receiver, uint256 minAlphaToReceive) external payable {
        uint256 feeAmount = Math.mulDiv(msg.value, stakingFee, MAX_FEE);
        uint256 taoAmount = msg.value - feeAmount;

        if (taoAmount < minTxSize) revert TxTooSmall();

        if (alphaTokens[netuid][hotkey] == address(0)) {
            _deployNewAlphaToken(netuid, hotkey);
        }

        uint256 alphaReceived = _addStake(hotkey, netuid, taoAmount);

        if (alphaReceived < minAlphaToReceive) {
            revert InsufficientAmountOut();
        }

        IAlphaToken(alphaTokens[netuid][hotkey]).mint(receiver, alphaReceived);
        emit Staked(msg.sender, netuid, hotkey, taoAmount, feeAmount, alphaReceived);
    }

    /**
     * @notice Burns a user's AlphaTokens and sends the corresponding TAO back to them.
     * @dev This function is protected against reentrancy attacks and includes a slippage guard.
     * @param hotkey The hotkey of the validator/miner to unstake from.
     * @param netuid The network UID of the subnet.
     * @param amount The amount of AlphaTokens to burn.
     * @param receiver The address that will receive the unstaked TAO.
     * @param minAmountTaoReceived The minimum amount of TAO expected by the user.
     */
    function unstake(bytes32 hotkey, uint256 netuid, uint256 amount, address receiver, uint256 minAmountTaoReceived)
        external
        nonReentrant
    {
        if (amount == 0) revert InvalidAmount();

        // Interaction: Call the precompile to remove stake
        uint256 taoReceived = _removeStake(hotkey, netuid, amount);
        uint256 feeAmount = Math.mulDiv(taoReceived, unstakingFee, MAX_FEE);
        taoReceived -= feeAmount;

        if (taoReceived < minTxSize) revert TxTooSmall();

        // Slippage protection
        if (taoReceived < minAmountTaoReceived) revert InsufficientTaoReceived(taoReceived, minAmountTaoReceived);

        // Effect: Burn the tokens first to prevent reentrancy abuse
        IAlphaToken(alphaTokens[netuid][hotkey]).burn(msg.sender, amount);

        // Interaction: Send TAO safely to the receiver
        (bool success,) = receiver.call{value: taoReceived}("");
        if (!success) revert EtherTransferFailed();

        emit Unstaked(msg.sender, netuid, hotkey, amount, taoReceived, feeAmount);
    }

    /**
     * @dev Deploys a new AlphaToken contract for a given subnet.
     * @param netuid The network UID to deploy a token for.
     * @return The address of the newly deployed token contract.
     */
    function _deployNewAlphaToken(uint256 netuid, bytes32 hotkey) internal returns (address) {
        string memory name = string(abi.encodePacked("SN-", Strings.toString(netuid)));
        address alphaToken = IAlphaTokenFactory(alphaTokenFactory).deployNewAlphaToken(name, name, netuid, hotkey);
        alphaTokens[netuid][hotkey] = alphaToken;

        emit AlphaTokenDeployed(netuid, alphaToken);
        return alphaToken;
    }

    /**
     * @dev Internal function to add stake to the precompile.
     * @return alphaAmount The amount of stake added, in AlphaToken denomination.
     */
    function _addStake(bytes32 hotkey, uint256 netuid, uint256 taoAmount) internal returns (uint256 alphaAmount) {
        IERC20 alphaToken = IERC20(alphaTokens[netuid][hotkey]);
        uint256 alphaBalanceBefore = _getStake(netuid, hotkey);
        uint256 totalSharesBefore = alphaToken.totalSupply();

        // The amount is sent via msg.value to this contract, then forwarded here.
        (bool success,) = stakingPrecompile.call(
            abi.encodeWithSelector(IStakingV2.addStake.selector, hotkey, taoAmount / RATIO_TAO_TO_ALPHA, netuid)
        );

        if (!success) {
            revert AddStakeFailed();
        }

        uint256 alphaBalanceAfter = _getStake(netuid, hotkey);
        uint256 alphaReceived = alphaBalanceAfter - alphaBalanceBefore;

        uint256 sharesToMint = Math.mulDiv(alphaReceived, totalSharesBefore + 1, alphaBalanceBefore + 1);

        emit StakeAdded(netuid, hotkey, alphaReceived, sharesToMint);
        return sharesToMint;
    }

    /**
     * @dev Internal function to remove stake from the precompile.
     * @return taoAmount The amount of TAO received from unstaking.
     */
    function _removeStake(bytes32 hotkey, uint256 netuid, uint256 amount) internal returns (uint256 taoAmount) {
        uint256 taoBalanceBefore = address(this).balance;
        IERC20 alphaToken = IERC20(alphaTokens[netuid][hotkey]);

        uint256 totalShares = alphaToken.totalSupply();
        uint256 totalAssets = _getStake(netuid, hotkey);

        uint256 alphaToUnstake = Math.mulDiv(amount, totalAssets + 1, totalShares + 1);

        (bool success,) = stakingPrecompile.call(
            abi.encodeWithSelector(IStakingV2.removeStake.selector, hotkey, alphaToUnstake, netuid)
        );

        if (!success) {
            revert RemoveStakeFailed();
        }

        uint256 taoBalanceAfter = address(this).balance;

        emit StakeRemoved(netuid, hotkey, alphaToUnstake, amount);
        return taoBalanceAfter - taoBalanceBefore;
    }

    /**
     * @dev Internal function to get the stake of a hotkey.
     * @param netuid The network UID of the subnet.
     * @param hotkey The hotkey of the validator/miner to get the stake of.
     * @return amount The amount of stake in TAO.
     */
    function _getStake(uint256 netuid, bytes32 hotkey) internal view returns (uint256 amount) {
        return IStakingV2(stakingPrecompile).getStake(hotkey, pubKey, netuid);
    }
}

contracts/STAO.sol.content:
// SPDX-License-Identifier: ISC
pragma solidity ^0.8.0;

import {ERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import {IStaking, ISUBTENSOR_STAKING_ADDRESS} from "./interfaces/IStaking.sol";
import {ISTAO} from "./interfaces/ISTAO.sol";
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
import {TAOStaker} from "./TAOStaker.sol";

/**
 * @title Staked TAO (sTAO)
 * @author kitanovicd (TaoFi)
 * @notice This contract handles the liquid staking token sTAO. It allows users to deposit TAO
 * and receive sTAO, which represents their share of the total staked TAO pool. Users can
 * later withdraw their TAO by burning sTAO.
 * @dev This is an upgradeable contract using OpenZeppelin's proxy pattern. It inherits staking
 * logic from TAOStaker.
 */
contract STAO is ERC20Upgradeable, OwnableUpgradeable, ISTAO, TAOStaker {
    using Math for uint256;

    /// @notice The public key of the contract used for staking. (Deprecated in V2)
    bytes32 public pubKey;
    /// @notice The address of the staking precompile contract. (Deprecated in V2)
    address public stakingPrecompile;
    /// @notice The fee taken from each deposit, denominated in TAO (wei).
    uint256 public networkFee;

    /**
     * @notice Initializes the contract for the first time.
     * @dev Sets up the ERC20 token details, ownership, and the initial TAOStaker state.
     * This function can only be called once.
     */
    function initialize() public initializer {
        __ERC20_init("Staked TAO", "sTAO");
        __Ownable_init();
        __TAOStaker_init(bytes32(0), address(0));
    }

    /**
     * @notice Initializes the V2 version of the contract.
     * @dev This reinitializer sets the pubKey and stakingPrecompile for the TAOStaker logic.
     * It's intended to be called during an upgrade to V2.
     * @param _pubKey The public key to be used for staking operations.
     * @param _stakingPrecompile The address of the external staking precompile contract.
     */
    function initializeV2(bytes32 _pubKey, address _stakingPrecompile) public reinitializer(2) {
        __TAOStaker_init(_pubKey, _stakingPrecompile);
    }

    /// @inheritdoc ISTAO
    function setNetworkFee(uint256 _networkFee) public onlyOwner {
        networkFee = _networkFee;
        emit NetworkFeeSet(_networkFee);
    }

    /**
     * @notice Deposits TAO into the contract and mints sTAO shares for the receiver.
     * @dev The function takes a network fee from the sent value, stakes the remainder,
     * calculates the corresponding sTAO shares, and mints them.
     * @param receiver The address that will receive the minted sTAO.
     * @param minSTAO The minimum amount of sTAO to be minted to protect against slippage.
     */
    function deposit(address receiver, uint256 minSTAO) public payable {
        require(msg.value >= networkFee, "Amount too low");

        uint256 amount = msg.value - networkFee;
        _stakeOnFirstHotKey(amount);

        uint256 sTAOAmount = convertToShares(amount, amount);
        require(sTAOAmount >= minSTAO, "Slippage too big");

        _mint(receiver, sTAOAmount);
        emit Deposit(msg.sender, receiver, amount, sTAOAmount);
    }

    /**
     * @notice Burns a specified amount of sTAO and transfers the corresponding amount of TAO to a receiver.
     * @dev If the contract's liquid TAO balance is insufficient, it will attempt to unstake
     * the required difference from the staking precompile.
     * @param amount The amount of sTAO to burn.
     * @param receiver The address to receive the TAO.
     * @param minTAO The minimum amount of TAO to be received to protect against slippage.
     */
    function withdraw(uint256 amount, address receiver, uint256 minTAO) external {
        require(balanceOf(msg.sender) >= amount, "Insufficient sTAO balance");

        uint256 taoAmount = convertToAssets(amount);
        require(taoAmount >= minTAO, "Slippage too big");

        uint256 taoAmountAvailable = address(this).balance;
        if (taoAmountAvailable < taoAmount) {
            _unstake(taoAmount - taoAmountAvailable);
        }

        require(address(this).balance >= taoAmount, "Insufficient TAO balance after unstaking");

        _burn(msg.sender, amount);
        payable(receiver).transfer(taoAmount);

        emit Withdrawal(msg.sender, receiver, taoAmount, amount);
    }

    /// @inheritdoc ISTAO
    function convertToShares(uint256 amount, uint256 totalStakedToDecrease) public view returns (uint256) {
        uint256 totalStaked = totalStakedTAO() - totalStakedToDecrease;
        // The +1 and +10**_decimalsOffset() are to prevent division by zero and ensure
        // a baseline share price when the pool is empty.
        return amount.mulDiv(totalSupply() + 10 ** _decimalsOffset(), totalStaked + 1, Math.Rounding.Down);
    }

    /// @inheritdoc ISTAO
    function convertToAssets(uint256 amount) public view returns (uint256) {
        uint256 supply = totalSupply();
        if (supply == 0) {
            return amount;
        }
        // The +1 and +10**_decimalsOffset() are to prevent division by zero and ensure
        // a baseline share price when the pool is empty.
        return amount.mulDiv(totalStakedTAO() + 1, supply + 10 ** _decimalsOffset(), Math.Rounding.Down);
    }

    /**
     * @dev Returns a decimal offset for share calculations. Currently 0.
     * This could be used in the future to handle tokens with different decimals.
     */
    function _decimalsOffset() private pure returns (uint256) {
        return 0;
    }
}

contracts/SwapAndStake.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IUniswapV3Router} from "./interfaces/IUniswapV3Router.sol";
import {IStakingManager} from "./interfaces/IStakingManager.sol";
import {IStakingV2} from "./interfaces/IStakingV2.sol";
import {IBridge} from "./interfaces/IBridge.sol";
import {IWTAO} from "./interfaces/IWTAO.sol";
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";

/**
 * @title SwapAndStake
 * @author kitanovicd (TaoFi)
 * @notice This contract allows users to perform a sequence of actions: swapping an ERC20 token (like USDC) for WTAO,
 * unwrapping it to native TAO, and staking it. It also provides functionality for the reverse process: unstaking,
 * swapping TAO back to an ERC20, and bridging the asset to another chain.
 */
contract SwapAndStake is Ownable {
    /// @notice Parameters for staking TAO.
    struct StakeParams {
        bytes32 hotkey;
        uint256 netuid;
        uint256 minAlphaToReceive;
        address receiver;
    }

    /// @notice Parameters for unstaking TAO.
    struct UnstakeParams {
        bytes32 hotkey;
        uint256 netuid;
        uint256 amount;
    }

    /// @notice Parameters for bridging assets to another chain.
    struct BridgeParams {
        uint256 bridgeFee;
        uint32 destinationChainId;
        bytes32 receiver;
    }

    /// @notice Parameters for UI fees.
    struct UiFeeParams {
        address receiver;
        uint256 feePercentage;
    }

    /**
     * @notice Emitted when a user successfully stakes TAO.
     * @param user The address of the user who initiated the staking.
     * @param hotkey The hotkey the TAO was staked to.
     * @param netuid The network UID of the subnet.
     * @param amount The amount of TAO that was staked.
     */
    event Stake(
        address indexed user,
        bytes32 indexed hotkey,
        uint256 netuid,
        uint256 amount,
        address feeReceiver,
        uint256 feeAmount
    );
    event Unstake(
        address indexed user,
        bytes32 indexed hotkey,
        uint256 netuid,
        uint256 amount,
        address feeReceiver,
        uint256 feeAmount
    );
    event Refund(
        address indexed user,
        address indexed receiver,
        uint256 amount,
        uint256 bridgeFee
    );

    error INVALID_SWAP_TOKENOUT();
    error INVALID_SWAP_TOKENIN();
    error INVALID_SWAP_AMOUNT();

    /**
     * @dev Allows the contract to receive the native asset (e.g., ETH or TAO).
     */
    receive() external payable {}

    uint256 public constant PERCENTAGE_FACTOR = 100_00;

    /// @notice The address of the USDC token contract.
    address public immutable usdc;
    /// @notice The address of the Wrapped TAO (WTAO) token contract.
    address public immutable wtao;
    /// @notice The address of the Uniswap V3 Router contract.
    address public immutable uniswapRouter;
    /// @notice The address of the StakingV2 precompile contract.
    address public immutable stakingPrecompile;
    /// @notice The address of the cross-chain bridge contract.
    address public immutable bridge;
    /// @notice The address of the Staking Manager contract.
    address public immutable stakingManager;

    /**
     * @notice Initializes the contract with the necessary external contract addresses.
     * @param _usdc The address of the USDC token.
     * @param _wtao The address of the WTAO token.
     * @param _uniswapRouter The address of the Uniswap V3 Router.
     * @param _stakingPrecompile The address of the StakingV2 precompile.
     * @param _bridge The address of the bridge contract.
     * @param _stakingManager The address of the Staking Manager contract.
     */
    constructor(
        address _usdc,
        address _wtao,
        address _uniswapRouter,
        address _stakingPrecompile,
        address _bridge,
        address _stakingManager
    ) {
        usdc = _usdc;
        wtao = _wtao;
        uniswapRouter = _uniswapRouter;
        stakingPrecompile = _stakingPrecompile;
        bridge = _bridge;
        stakingManager = _stakingManager;
    }

    /**
     * @notice Swaps a specified amount of an input token for TAO and stakes it.
     * @dev The caller must have approved this contract to spend their input tokens.
     *      Note: This function will override swapParams.amountIn to use the user's entire
     *      balance of the input token and swapParams.recipient to ensure tokens are received
     *      by this contract for staking.
     * @param swapParams The parameters for the Uniswap V3 swap.
     * @param stakeParams The parameters for staking, including hotkey and netuid.
     * @param uiFeeParams The parameters for the UI fee.
     */
    function swapAndStake(
        IUniswapV3Router.ExactInputSingleParams memory swapParams,
        StakeParams calldata stakeParams,
        UiFeeParams calldata uiFeeParams
    ) external {
        swapParams.amountIn = Math.min(swapParams.amountIn, IERC20(swapParams.tokenIn).balanceOf(address(msg.sender)));
        swapParams.recipient = address(this);

        // Take USDC
        SafeERC20.safeTransferFrom(IERC20(swapParams.tokenIn), msg.sender, address(this), swapParams.amountIn);

        // Swap USDC to TAO
        IERC20(swapParams.tokenIn).approve(uniswapRouter, swapParams.amountIn);
        uint256 amountOut = IUniswapV3Router(uniswapRouter).exactInputSingle(swapParams);

        uint256 feeAmount = amountOut * uiFeeParams.feePercentage / PERCENTAGE_FACTOR;
        amountOut -= feeAmount;

        IERC20(wtao).transfer(uiFeeParams.receiver, feeAmount);

        // If asset out is WTAO, unwrap it
        if (swapParams.tokenOut == wtao) {
            IWTAO(wtao).withdraw(amountOut);
        }

        // no receiver means ICA will hold alpha token.
        address alphaTokenReceiver = stakeParams.receiver == address(0) ? msg.sender : stakeParams.receiver;

        IStakingManager(stakingManager).stake{value: amountOut}(
            stakeParams.hotkey, stakeParams.netuid, alphaTokenReceiver, stakeParams.minAlphaToReceive
        );

        emit Stake(alphaTokenReceiver, stakeParams.hotkey, stakeParams.netuid, amountOut, uiFeeParams.receiver, feeAmount);
    }

    /**
     * @notice Unstakes TAO, swaps it for an ERC20 token (e.g., USDC), and bridges the token to another chain.
     * @dev The user must have approved this contract to spend their alpha (staking) tokens. This function is payable
     * to allow the user to send native assets to cover any bridge fees.
     * @param unstakeParams The parameters for unstaking from the subnet.
     * @param swapParams The parameters for the Uniswap V3 swap (WTAO to USDC).
     * @param bridgeParams The parameters for the cross-chain bridge transfer.
     * @param uiFeeParams Parameters for calculating and forwarding the UI fee.
     */
    function unstakeSwapAndBridge(
        UnstakeParams calldata unstakeParams,
        IUniswapV3Router.ExactInputSingleParams memory swapParams,
        BridgeParams calldata bridgeParams,
        UiFeeParams calldata uiFeeParams
    ) external {
        if (swapParams.tokenIn != wtao) revert INVALID_SWAP_TOKENIN();
        if (swapParams.tokenOut != usdc) revert INVALID_SWAP_TOKENOUT();

        // Unstake TAO
        address alphaToken = IStakingManager(stakingManager).alphaTokens(unstakeParams.netuid, unstakeParams.hotkey);

        IERC20(alphaToken).transferFrom(msg.sender, address(this), unstakeParams.amount);

        uint256 taoBalanceBefore = address(this).balance;

        // Since the minimum output check (amountOutMinimum) is already enforced externally via swapParams,
        // we can safely pass minAmountTaoReceived = 0 here to allow unstaking without redundant validation.
        IStakingManager(stakingManager).unstake(
            unstakeParams.hotkey, unstakeParams.netuid, unstakeParams.amount, address(this), 0
        );

        uint256 taoBalanceAfter = address(this).balance;
        uint256 taoReceived = taoBalanceAfter - taoBalanceBefore;

        // Wrap TAO to WTAO
        IWTAO(wtao).deposit{value: taoReceived - bridgeParams.bridgeFee}();

        uint256 feeAmount = taoReceived * uiFeeParams.feePercentage / PERCENTAGE_FACTOR;
        taoReceived -= feeAmount;
        taoReceived -= bridgeParams.bridgeFee;

        IERC20(wtao).transfer(uiFeeParams.receiver, feeAmount);

        // Approve and swap
        IERC20(wtao).approve(uniswapRouter, taoReceived);

        swapParams.amountIn = taoReceived;

        // Swap WTAO to USDC
        uint256 amountOut = IUniswapV3Router(uniswapRouter).exactInputSingle(swapParams);

        IERC20(usdc).approve(bridge, amountOut);

        IBridge(bridge).transferRemote{value: bridgeParams.bridgeFee}(
            bridgeParams.destinationChainId, bridgeParams.receiver, amountOut
        );

        emit Unstake(
            msg.sender,
            unstakeParams.hotkey,
            unstakeParams.netuid,
            unstakeParams.amount,
            uiFeeParams.receiver,
            feeAmount
        );
    }

    /**
     * @notice Allows refunding users sending native USDC to a receiver on a remote chain.
     * @dev Requires prior approval of USDC by the user. The bridge fee is enforced to match the output amount from Uniswap.
     * @param swapParams Uniswap V3 exact output parameters to swap USDC to WTAO (covering the bridge fee).
     * @param bridgeParams Parameters for cross-chain transfer, including receiver, destination chain ID, and bridge fee.
     * @param amount Total USDC amount being refunded by the caller (portion goes to swap, rest is bridged).
     */
    function refund(
        IUniswapV3Router.ExactOutputSingleParams calldata swapParams,
        BridgeParams calldata bridgeParams,
        uint256 amount
    ) external {
        if (swapParams.tokenIn != usdc) revert INVALID_SWAP_TOKENIN();
        if (swapParams.tokenOut != wtao) revert INVALID_SWAP_TOKENOUT();
        if (bridgeParams.bridgeFee != swapParams.amountOut) revert INVALID_SWAP_AMOUNT();

        IERC20(usdc).transferFrom(msg.sender, address(this), amount);

        // Approve and swap
        IERC20(usdc).approve(uniswapRouter, swapParams.amountInMaximum);
        // Swap USDC to WTAO
        uint256 amountIn = IUniswapV3Router(uniswapRouter).exactOutputSingle(swapParams);

        // Unwrap WTAO to TAO
        IWTAO(wtao).withdraw(bridgeParams.bridgeFee);

        IBridge(bridge).transferRemote{value: bridgeParams.bridgeFee}(
            bridgeParams.destinationChainId, bridgeParams.receiver, amount - amountIn
        );

        emit Refund(
            msg.sender,
            address(uint160(uint256(bridgeParams.receiver))),
            amount,
            bridgeParams.bridgeFee
        );
    }
}

contracts/SwapAndTransfer.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IUniswapV3Router} from "./interfaces/IUniswapV3Router.sol";
import {IBalanceTransfer} from "./interfaces/IBalanceTransfer.sol";
import {IWTAO} from "./interfaces/IWTAO.sol";
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";

/**
 * @title SwapAndTransfer
 * @author Jason (TaoFi)
 * @notice This contract allows users to perform a sequence of actions: swapping an ERC20 token (like USDC) for WTAO,
 * unwrapping it to native TAO, and transfer it to bittensor wallet. It also provides functionality for swapping TAO 
 * back to an ERC20, and bridging the asset to another chain.
 */
contract SwapAndTransfer {
    /// @notice Parameters for UI fees.
    struct UiFeeParams {
        address receiver;
        uint256 feePercentage;
    }

    event TaoTransfer(
        bytes32 indexed user,
        uint256 amount
    );

    error INVALID_FEE();
    error TRANSFER_FAILED();

    /**
     * @dev Allows the contract to receive the native asset (e.g., ETH or TAO).
     */
    receive() external payable {}

    uint256 public constant PERCENTAGE_FACTOR = 100_00;

    /// @notice The address of the Wrapped TAO (WTAO) token contract.
    address public immutable wtao;
    /// @notice The address of the Uniswap V3 Router contract.
    address public immutable uniswapRouter;
    /// @notice The address of the BalanceTransfer precompile contract.
    address public immutable transferPrecompile;

    /**
     * @notice Initializes the contract with the necessary external contract addresses.
     * @param _wtao The address of the WTAO token.
     * @param _uniswapRouter The address of the Uniswap V3 Router.
     * @param _transferPrecompile The address of the BalanceTransfer precompile.
     */
    constructor(
        address _wtao,
        address _uniswapRouter,
        address _transferPrecompile
    ) {
        wtao = _wtao;
        uniswapRouter = _uniswapRouter;
        transferPrecompile = _transferPrecompile;
    }

    /**
     * @notice Swaps a specified amount of an input token for TAO and transfer it to bittensor wallet.
     * @dev The caller must have approved this contract to spend their input tokens.
     *      Note: This function will override swapParams.amountIn to use the user's entire
     *      balance of the input token and swapParams.recipient to ensure tokens are received
     *      by this contract for staking.
     * @param swapParams The parameters for the Uniswap V3 swap.
     * @param uiFeeParams The parameters for the UI fee.
     * @param receiver The parameters for TAO receiver of bittensor wallet.
     */
    function swapAndTransfer(
        IUniswapV3Router.ExactInputSingleParams memory swapParams,
        UiFeeParams calldata uiFeeParams,
        bytes32 receiver
    ) external {
        swapParams.amountIn = Math.min(swapParams.amountIn, IERC20(swapParams.tokenIn).balanceOf(address(msg.sender)));
        swapParams.recipient = address(this);

        // Take USDC
        SafeERC20.safeTransferFrom(IERC20(swapParams.tokenIn), msg.sender, address(this), swapParams.amountIn);

        // Swap USDC to TAO
        IERC20(swapParams.tokenIn).approve(uniswapRouter, swapParams.amountIn);
        uint256 amountOut = IUniswapV3Router(uniswapRouter).exactInputSingle(swapParams);

        uint256 feeAmount = amountOut * uiFeeParams.feePercentage / PERCENTAGE_FACTOR;
        amountOut -= feeAmount;

        IERC20(wtao).transfer(uiFeeParams.receiver, feeAmount);

        // If asset out is WTAO, unwrap it
        if (swapParams.tokenOut == wtao) {
            IWTAO(wtao).withdraw(amountOut);
        }

        // Transfer TAO
        (bool success,) = transferPrecompile.call{ value: amountOut }(
            abi.encodeWithSelector(IBalanceTransfer.transfer.selector, receiver)
        );
        if (!success) {
            revert TRANSFER_FAILED();
        }

        emit TaoTransfer(receiver, amountOut);
    }
}

contracts/SwapBridgeAndCallFromMain.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.21;

import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {IBridge} from "./interfaces/IBridge.sol";
import {
    IInterchainAccountRouter,
    IInterchainAccountRouterWithOverrides,
    Call
} from "./interfaces/IInterchainAccountRouter.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

/// @title SwapBridgeAndCallFromMain
/// @author Jason (Sturdy) https://github.com/iris112
/// @notice ERC20/ETH -> BridgeToken(Ethereum) -> BridgeToken(Bittensor EVM) -> Remote Call(Bittensor EVM)
contract SwapBridgeAndCallFromMain is Initializable, OwnableUpgradeable, ReentrancyGuardUpgradeable {
    using SafeERC20 for IERC20;

    struct SwapParams {
        address fromToken;
        uint256 fromAmount;
        address approvalAddress;
        address target;
        bytes data;
    }

    struct RemoteCallsParams {
        bytes32 router;
        bytes32 ism;
        Call[] calls;
        bytes hookMetadata;
    }

    struct ExternalCall {
        address target;
        uint256 value;
        bytes data;
    }

    uint256 private constant PERCENTAGE_FACTOR = 100_00;
    uint32 private constant DESTINATION_CHAIN_ID = 964;

    address public bridgeToken;
    address public bridge;
    address public interchainAccountRouter;
    address public treasury;

    mapping(address => bool) public isTargetAddressWhitelisted;

    // to => selector => allowed
    mapping(bytes32 => mapping(bytes4 => bool)) public allowedRemoteCalls;

    event BridgeTokenUpdated(address newBridgeToken);
    event BridgeUpdated(address newBridge);
    event InterchainAccountRouterUpdated(address newInterchainAccountRouter);
    event SwapAndBridgeExecuted(address indexed target, bytes data);
    event TargetAddressWhitelisted(address indexed target);
    event AllowedRemoteCallUpdated(bytes32 indexed target, bytes4 indexed selector, bool allowed);

    error INVALID_ADDRESS();
    error INVALID_VALUE();
    error NOT_CONTRACT();
    error SWAP_FAILED();
    error BRIDGE_FAILED();
    error EXTERNAL_CALL_FAILED();
    error INVALID_TARGET();
    error UNAUTHORIZED_CALL_TYPE();

    constructor() {
        _disableInitializers();
    }

    function initialize() external initializer {
        __Ownable_init();
        __ReentrancyGuard_init();
    }

    /**
     * @dev Set the bridge token contract address.
     * @param _bridgeToken - The address of the bridge token contract.
     */
    function setBridgeToken(address _bridgeToken) external payable onlyOwner {
        require(_bridgeToken.code.length > 0, "Invalid bridge token address");
        bridgeToken = _bridgeToken;

        emit BridgeTokenUpdated(_bridgeToken);
    }

    /**
     * @dev Set the bridge contract address.
     * @param _bridge - The address of the bridge contract.
     */
    function setBridge(address _bridge) external payable onlyOwner {
        require(_bridge.code.length > 0, "Invalid bridge address");
        bridge = _bridge;

        emit BridgeUpdated(_bridge);
    }

    /**
     * @dev Set the interchainAccountRouter contract address which is for remote call.
     * @param _interchainAccountRouter - The address of the interchainAccountRouter contract
     */
    function setInterchainAccountRouter(address _interchainAccountRouter) external payable onlyOwner {
        require(_interchainAccountRouter.code.length > 0, "Invalid interchain account router address");
        interchainAccountRouter = _interchainAccountRouter;

        emit InterchainAccountRouterUpdated(_interchainAccountRouter);
    }

    /**
     * @dev Set the target address to be blacklisted.
     * @param _target - The address of the target.
     * @param _isWhitelisted - The boolean value to determine if the target is blacklisted.
     */
    function setTargetAddressWhitelisted(address _target, bool _isWhitelisted) external payable onlyOwner {
        isTargetAddressWhitelisted[_target] = _isWhitelisted;

        emit TargetAddressWhitelisted(_target);
    }

    /**
     * @dev Adds or removes an allowed remote function selector for a given target.
     * @param _target The target address (as bytes32) to control permissions for.
     * @param _selector The 4-byte function selector to allow or disallow.
     * @param _allowed A boolean indicating whether the call is permitted.
     */
    function setAllowedRemoteCall(bytes32 _target, bytes4 _selector, bool _allowed) external payable onlyOwner {
        allowedRemoteCalls[_target][_selector] = _allowed;

        emit AllowedRemoteCallUpdated(_target, _selector, _allowed);
    }

    /**
     * @dev Executes a token swap via LiFi, bridge to Bittensor EVM and call remote function of Bittensor EVM contract.
     *      ex: ERC20/ETH(Ethereum) -> USDC(Ethereum) -> USDC(Bittensor EVM) -> Remote Call(Bittensor EVM)
     * @param _swapParams The parameters for the swap. Including the from token, from amount, approval address, target and data.
     * @param _params The parameters for the remote call.
     * @param _bridgeCost The cost of the bridge.
     */
    function lifiSwapBridgeAndCall(
        SwapParams calldata _swapParams,
        RemoteCallsParams calldata _params,
        uint256 _bridgeCost
    ) external payable nonReentrant {
        address _fromToken = _swapParams.fromToken;
        uint256 _fromAmount = _swapParams.fromAmount;
        address _approvalAddress = _swapParams.approvalAddress;
        address _target = _swapParams.target;
        bytes calldata _data = _swapParams.data;

        if (_fromAmount == 0) revert INVALID_VALUE();

        uint256 valueSpent = _fromToken == address(0) ? _fromAmount : 0;
        address _toToken = bridgeToken;

        if (_fromToken == _toToken) {
            // No need LiFi Swap and just transfer
            IERC20(_fromToken).safeTransferFrom(msg.sender, address(this), _fromAmount);
        } else {
            if (_approvalAddress == address(0)) revert INVALID_ADDRESS();
            if (_target == address(0)) revert INVALID_ADDRESS();
            if (!Address.isContract(_target)) revert NOT_CONTRACT();

            if (_fromToken == address(0)) {
                // fromToken is ETH
                if (msg.value < _fromAmount + _bridgeCost) revert SWAP_FAILED();

                // LiFi Swap
                _executeExternalCall(_target, _fromAmount, _data);
            } else {
                // transfer
                IERC20(_fromToken).safeTransferFrom(msg.sender, address(this), _fromAmount);

                // Approve
                IERC20(_fromToken).forceApprove(_approvalAddress, _fromAmount);

                // LiFi Swap
                _executeExternalCall(_target, 0, _data);

                uint256 remainingBalance = IERC20(_fromToken).balanceOf(address(this));
                if (remainingBalance > 0) revert SWAP_FAILED();
            }

            emit SwapAndBridgeExecuted(_target, _data);
        }

        _bridgeAndCall(_params, valueSpent, _bridgeCost);
    }

    /**
     * @dev Executes a dest chain function to process exception case
     * @param _params The call data array for the remote call of the dest chain.
     */
    function remoteCall(RemoteCallsParams calldata _params) external payable nonReentrant {
        uint256 callCnt = _params.calls.length;
        for (uint256 i; i < callCnt; ++i) {
            if (!isAllowedRemoteCall(_params.calls[i])) revert UNAUTHORIZED_CALL_TYPE();
        }

        bytes32 userSpecificSalt = bytes32(uint256(uint160(msg.sender)));
        IInterchainAccountRouterWithOverrides(interchainAccountRouter).callRemoteWithOverrides{value: msg.value}(
            DESTINATION_CHAIN_ID,
            _params.router, // Or your bytes32 router override
            _params.ism, // Or your bytes32 ISM override
            _params.calls,
            _params.hookMetadata,
            userSpecificSalt
        );
    }

    /**
     * @dev Allows the contract owner to recover ERC20 tokens that were mistakenly sent to this contract.
     *      Can be used in emergency cases to transfer out stuck tokens.
     * @param token The address of the ERC20 token to recover.
     * @param to The address that will receive the recovered tokens.
     * @param amount The amount of tokens to transfer.
     */
    function emergencyTokenRecovery(address token, address to, uint256 amount) external payable onlyOwner {
        IERC20(token).safeTransfer(to, amount);
    }

    /**
     * @dev Allows the contract owner to recover native ETH that was mistakenly sent to this contract.
     *      Can be used in emergency cases to transfer out stuck ETH.
     * @param to The address that will receive the recovered ETH.
     * @param amount The amount of ETH to transfer.
     */
    function emergencyETHRecovery(address to, uint256 amount) external payable onlyOwner {
        (bool success,) = to.call{value: amount}("");
        require(success, "ETH transfer failed");
    }

    /**
     * @dev Checks whether a given remote call is allowed.
     *      Validates both the target and the function selector against the whitelist.
     * @param _call The call struct containing the target, value, and calldata.
     * @return True if the call is authorized, false otherwise.
     */
    function isAllowedRemoteCall(Call calldata _call) public view returns (bool) {
        if (_call.data.length < 4) return false; // Invalid call data

        return allowedRemoteCalls[_call.to][bytes4(_call.data)];
    }

    function _bridgeAndCall(RemoteCallsParams calldata _params, uint256 valueSpent, uint256 _bridgeCost) internal {
        uint256 toAmount = IERC20(bridgeToken).balanceOf(address(this));
        if (toAmount == 0) revert SWAP_FAILED();

        // fee processing

        IERC20(bridgeToken).forceApprove(bridge, toAmount);

        // Get the interchain account address for the contract on the destination chain
        bytes32 userSpecificSalt = bytes32(uint256(uint160(msg.sender)));

        // Avoid stack too deep
        {
            IInterchainAccountRouterWithOverrides ica = IInterchainAccountRouterWithOverrides(interchainAccountRouter);

            address routerAddress = address(uint160(uint256(_params.router)));
            address ismAddress = address(uint160(uint256(_params.ism)));
            address userIcaOnDestination =
                ica.getRemoteInterchainAccount(address(this), routerAddress, ismAddress, userSpecificSalt);

            // Bridge
            IBridge(bridge).transferRemote{value: _bridgeCost}(
                DESTINATION_CHAIN_ID, bytes32(uint256(uint160(userIcaOnDestination))), toAmount
            );
        }

        // Execute the specified interchain calls using the remaining gas funds
        IInterchainAccountRouterWithOverrides(interchainAccountRouter).callRemoteWithOverrides{
            value: msg.value - valueSpent - _bridgeCost
        }(
            DESTINATION_CHAIN_ID,
            _params.router, // Or your bytes32 router override
            _params.ism, // Or your bytes32 ISM override
            _params.calls,
            _params.hookMetadata,
            userSpecificSalt
        );
    }

    function _executeExternalCall(address target, uint256 value, bytes calldata data) internal {
        if (!isTargetAddressWhitelisted[target]) {
            revert INVALID_TARGET();
        }

        bool success;

        if (value == 0) {
            (success,) = target.call(data);
        } else {
            (success,) = target.call{value: value}(data);
        }

        if (!success) revert EXTERNAL_CALL_FAILED();
    }
}

contracts/TAOStaker.sol.content:
// SPDX-License-Identifier: ISC
pragma solidity ^0.8.21;

import "hardhat/console.sol";

import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
import {IStaking} from "./interfaces/IStaking.sol";
import {ITAOStaker} from "./interfaces/ITAOStaker.sol";

contract TAOStaker is OwnableUpgradeable, ITAOStaker {
    struct TAOSTakerStorage {
        /// @notice Pubkey of this contract
        bytes32 pubKey;
        /// @notice Staking precompile address
        address stakingPrecompile;
        /// @notice Array of hotkeys to delegate to
        bytes32[] hotkeys;
        /// @notice Mapping of hotkeys to check if they exist in the hotkeys array
        mapping(bytes32 => bool) isHotKeyAdded;
    }

    function _getTAOStakerStorage() internal pure returns (TAOSTakerStorage storage s) {
        bytes32 slot =
            keccak256(abi.encode(uint256(keccak256("taofi.contracts.storage.TAOStaker")) - 1)) & ~bytes32(uint256(0xff));

        assembly {
            s.slot := slot
        }
    }

    receive() external payable {}

    function __TAOStaker_init(bytes32 pubKey, address stakingPrecompile) internal {
        TAOSTakerStorage storage $ = _getTAOStakerStorage();
        $.pubKey = pubKey;
        $.stakingPrecompile = stakingPrecompile;

        emit PubKeySet(pubKey);
        emit StakingPrecompileSet(stakingPrecompile);
    }

    /// @inheritdoc ITAOStaker
    function getPubKey() public view returns (bytes32) {
        return _getTAOStakerStorage().pubKey;
    }

    /// @inheritdoc ITAOStaker
    function getStakingPrecompile() public view returns (address) {
        return _getTAOStakerStorage().stakingPrecompile;
    }

    /// @inheritdoc ITAOStaker
    function getHotkeys() public view returns (bytes32[] memory) {
        return _getTAOStakerStorage().hotkeys;
    }

    /// @inheritdoc ITAOStaker
    function getHotKey(uint256 index) public view returns (bytes32) {
        return _getTAOStakerStorage().hotkeys[index];
    }

    /// @inheritdoc ITAOStaker
    function getHotKeyStakedAmount(bytes32 hotkey) public view returns (uint256) {
        return IStaking(getStakingPrecompile()).getStake(hotkey, getPubKey(), 0);
    }

    /// @inheritdoc ITAOStaker
    function isHotKeyAdded(bytes32 hotkey) public view returns (bool) {
        return _getTAOStakerStorage().isHotKeyAdded[hotkey];
    }

    /// @inheritdoc ITAOStaker
    function totalStakedTAO() public view returns (uint256 totalStaked) {
        bytes32[] memory hotkeys = _getTAOStakerStorage().hotkeys;
        for (uint256 i = 0; i < hotkeys.length; i++) {
            totalStaked += getHotKeyStakedAmount(hotkeys[i]);
        }

        return totalStaked + address(this).balance;
    }

    /// @inheritdoc ITAOStaker
    function setPubKey(bytes32 pubKey) public onlyOwner {
        _getTAOStakerStorage().pubKey = pubKey;
        emit PubKeySet(pubKey);
    }

    /// @inheritdoc ITAOStaker
    function setStakingPrecompile(address stakingPrecompile) public onlyOwner {
        _getTAOStakerStorage().stakingPrecompile = stakingPrecompile;
        emit StakingPrecompileSet(stakingPrecompile);
    }

    /// @inheritdoc ITAOStaker
    function setHotKeyAsPriority(uint256 hotkeyIndex) public onlyOwner {
        bytes32 newPriorityHotKey = getHotKey(hotkeyIndex);

        TAOSTakerStorage storage $ = _getTAOStakerStorage();
        $.hotkeys[hotkeyIndex] = $.hotkeys[0];
        $.hotkeys[0] = newPriorityHotKey;

        emit HotKeyPrioritySet(hotkeyIndex);
    }

    /// @inheritdoc ITAOStaker
    function rebalance(uint256[] calldata amounts) public onlyOwner {
        // increase/decrease the stakes according to the hotkeys and amounts
        // make it so that the final stake in the hotkeys is the amount specified
        bytes32[] memory hotkeys = getHotkeys();

        for (uint256 i = 0; i < hotkeys.length; i++) {
            uint256 currentStake = getHotKeyStakedAmount(hotkeys[i]);
            if (currentStake < amounts[i]) {
                _addStake(hotkeys[i], amounts[i] - currentStake);
            } else if (currentStake > amounts[i]) {
                _removeStake(hotkeys[i], currentStake - amounts[i]);
            }
        }
    }

    /// @inheritdoc ITAOStaker
    function addHotKeys(bytes32[] calldata hotkeys) public onlyOwner {
        for (uint256 i = 0; i < hotkeys.length; i++) {
            bytes32 hotkey = hotkeys[i];

            if (isHotKeyAdded(hotkey)) {
                revert HotKeyAlreadyAdded(hotkey);
            }

            _getTAOStakerStorage().hotkeys.push(hotkey);
            _getTAOStakerStorage().isHotKeyAdded[hotkey] = true;
        }

        emit HotkeysAdded(hotkeys);
    }

    /// @inheritdoc ITAOStaker
    function removeHotKeys(uint256[] calldata hotKeysIndexes) public onlyOwner {
        for (uint256 i = 0; i < hotKeysIndexes.length; i++) {
            uint256 index = hotKeysIndexes[i];

            // If hotkey is not added, this function call will revert so there is not need for special case handling
            // but we can check just in case
            bytes32 hotkey = getHotKey(index);
            if (!isHotKeyAdded(hotkey)) {
                revert HotKeyNotAdded(hotkey);
            }

            _removeStake(hotkey, getHotKeyStakedAmount(hotkey));

            // Remove the hotkey from the array
            TAOSTakerStorage storage $ = _getTAOStakerStorage();
            uint256 lastIndex = $.hotkeys.length - 1;

            // If the element to remove is not the last one, move the last element to the position being removed
            if (index < lastIndex) {
                $.hotkeys[index] = $.hotkeys[lastIndex];
            }

            // Remove the last element
            $.hotkeys.pop();

            // Update the mapping
            $.isHotKeyAdded[hotkey] = false;

            emit HotkeyRemoved(hotkey);
        }
    }

    /// @notice Removes from all hotkeys until the given amount is unstaken
    /// @param amount The amount to unstake
    /// @dev This function is used in order to make TAO liquid
    function _unstake(uint256 amount) internal {
        bytes32[] memory hotkeys = _getTAOStakerStorage().hotkeys;

        // Go through each hotkeys and unstake until full amount is unstaken
        // If there is not enough staked TAO this function will revert because of the overflow
        uint256 i = 0;
        while (amount > 0) {
            uint256 availableOnHotKey = getHotKeyStakedAmount(hotkeys[i]);
            uint256 amountToUnstake = Math.min(amount, availableOnHotKey);

            _removeStake(hotkeys[i], amountToUnstake);
            amount -= amountToUnstake;
            i++;
        }
    }

    /// @notice Stakes on the first hotkey
    /// @param amount The amount to stake
    /// @dev This function is used in order to always automatically stake TAO on the first hotkey
    function _stakeOnFirstHotKey(uint256 amount) internal {
        bytes32 hotkey = _getTAOStakerStorage().hotkeys[0];
        _addStake(hotkey, amount);
    }

    /// @notice Adds a stake to a hotkey
    /// @param hotkey The hotkey to add the stake to
    /// @param amount The amount to add
    function _addStake(bytes32 hotkey, uint256 amount) internal {
        IStaking(getStakingPrecompile()).addStake{value: amount}(hotkey, 0);
        emit StakeAdded(hotkey, amount);
    }

    /// @notice Removes a stake from a hotkey
    /// @param hotkey The hotkey to remove the stake from
    /// @param amount The amount to remove
    function _removeStake(bytes32 hotkey, uint256 amount) internal {
        IStaking(getStakingPrecompile()).removeStake(hotkey, amount, 0);
        emit StakeRemoved(hotkey, amount);
    }
}

