// SPDX-License-Identifier: AGPL-v3.0
pragma solidity ^0.8.21;

interface IUniswapV3Pool {
    function protocolFees() external view returns (uint128 token0, uint128 token1);
    function collectProtocol(
        address recipient,
        uint128 amount0Requested,
        uint128 amount1Requested
    ) external returns (uint128 amount0, uint128 amount1);
}

interface IUniswapV3Factory {
    function setOwner(address _owner) external;
}

contract UniV3PoolFeeClaimer {
    IUniswapV3Pool private constant POOL =
        IUniswapV3Pool(0x6647dcbeb030dc8E227D8B1A2Cb6A49F3C887E3c);
    IUniswapV3Factory private constant FACTORY =
        IUniswapV3Factory(0x20D0Cdf9004bf56BCa52A25C9288AAd0EbB97D59);
    address private constant RECEIVER = 0xb8dd0e5580BE15E56c6937EE0e4EaB3c6F4E9360;

    address public owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    constructor() {
        owner = msg.sender;
        emit OwnershipTransferred(address(0), msg.sender);
    }

    function claim() external {
        (uint128 fee0, uint128 fee1) = POOL.protocolFees();
        POOL.collectProtocol(RECEIVER, fee0, fee1);
    }

    function setFactoryOwner(address newOwner) external onlyOwner {
        FACTORY.setOwner(newOwner);
    }

    /**
     * @notice Transfer contract ownership
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Invalid owner");

        address oldOwner = owner;
        owner = newOwner;

        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

contracts/UnstakeAndSwap.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IUniswapV3Router} from "./interfaces/IUniswapV3Router.sol";
import {IStakingManager} from "./interfaces/IStakingManager.sol";
import {IBridge} from "./interfaces/IBridge.sol";
import {IWTAO} from "./interfaces/IWTAO.sol";
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";

/**
 * @title UnstakeAndSwap
 * @author Jason (TaoFi)
 * @notice This contract allows users to perform a unstaking, swapping TAO back to an ERC20, and 
 * bridging the asset to another chain.
 */
contract UnstakeAndSwap is Ownable {
    /// @notice Parameters for unstaking TAO.
    struct UnstakeParams {
        bytes32 hotkey;
        uint256 netuid;
        uint256 amount;
    }

    /// @notice Parameters for bridging assets to another chain.
    struct BridgeParams {
        uint256 bridgeFee;
        uint32 destinationChainId;
        bytes32 receiver;
    }

    /// @notice Parameters for UI fees.
    struct UiFeeParams {
        address receiver;
        uint256 feePercentage;
    }

    event Unstake(
        address indexed user,
        bytes32 indexed hotkey,
        uint256 netuid,
        uint256 amount,
        address feeReceiver,
        uint256 feeAmount
    );

    error INVALID_SWAP_TOKENOUT();
    error INVALID_SWAP_TOKENIN();
    error INVALID_SWAP_AMOUNT();

    /**
     * @dev Allows the contract to receive the native asset (e.g., ETH or TAO).
     */
    receive() external payable {}

    uint256 public constant PERCENTAGE_FACTOR = 100_00;

    /// @notice The address of the USDC token contract.
    address public immutable usdc;
    /// @notice The address of the Wrapped TAO (WTAO) token contract.
    address public immutable wtao;
    /// @notice The address of the Uniswap V3 Router contract.
    address public immutable uniswapRouter;
    /// @notice The address of the cross-chain bridge contract.
    address public immutable bridge;
    /// @notice The address of the Staking Manager contract.
    address public immutable stakingManager;
    /// @notice The user's available swap and bridge amount tracking.
    mapping(address => uint256) public userSwapAmount;

    /**
     * @notice Initializes the contract with the necessary external contract addresses.
     * @param _usdc The address of the USDC token.
     * @param _wtao The address of the WTAO token.
     * @param _uniswapRouter The address of the Uniswap V3 Router.
     * @param _bridge The address of the bridge contract.
     * @param _stakingManager The address of the Staking Manager contract.
     */
    constructor(
        address _usdc,
        address _wtao,
        address _uniswapRouter,
        address _bridge,
        address _stakingManager
    ) {
        usdc = _usdc;
        wtao = _wtao;
        uniswapRouter = _uniswapRouter;
        bridge = _bridge;
        stakingManager = _stakingManager;
    }

    /**
     * @notice Unstakes TAO, process fee.
     * @dev The user must have approved this contract to spend their alpha (staking) tokens.
     * @param unstakeParams The parameters for unstaking from the subnet.
     * @param uiFeeParams Parameters for calculating and forwarding the UI fee.
     */
    function unstake(
        UnstakeParams calldata unstakeParams,
        UiFeeParams calldata uiFeeParams
    ) external {
        // Unstake TAO
        address alphaToken = IStakingManager(stakingManager).alphaTokens(unstakeParams.netuid, unstakeParams.hotkey);

        IERC20(alphaToken).transferFrom(msg.sender, address(this), unstakeParams.amount);

        uint256 taoBalanceBefore = address(this).balance;

        // Since the minimum output check (amountOutMinimum) is already enforced externally via swapParams,
        // we can safely pass minAmountTaoReceived = 0 here to allow unstaking without redundant validation.
        IStakingManager(stakingManager).unstake(
            unstakeParams.hotkey, unstakeParams.netuid, unstakeParams.amount, address(this), 0
        );

        uint256 taoBalanceAfter = address(this).balance;
        uint256 taoReceived = taoBalanceAfter - taoBalanceBefore;
        uint256 feeAmount = taoReceived * uiFeeParams.feePercentage / PERCENTAGE_FACTOR;

        // Wrap TAO to WTAO
        IWTAO(wtao).deposit{value: feeAmount}();

        taoReceived -= feeAmount;
        userSwapAmount[msg.sender] += taoReceived;

        IERC20(wtao).transfer(uiFeeParams.receiver, feeAmount);

        emit Unstake(
            msg.sender,
            unstakeParams.hotkey,
            unstakeParams.netuid,
            unstakeParams.amount,
            uiFeeParams.receiver,
            feeAmount
        );
    }

    /**
     * @notice swaps the TAO for an ERC20 token (e.g., USDC), and bridges the token to another chain.
     * @dev This contract has TAO unstaked and wrapped as WTAO.
     * @param swapParams The parameters for the Uniswap V3 swap (WTAO to USDC).
     * @param bridgeParams The parameters for the cross-chain bridge transfer.
     */
    function swapAndBridge(
        IUniswapV3Router.ExactInputSingleParams memory swapParams,
        BridgeParams calldata bridgeParams
    ) external {
        uint256 taoReceived = userSwapAmount[msg.sender];

        if (swapParams.tokenIn != wtao) revert INVALID_SWAP_TOKENIN();
        if (swapParams.tokenOut != usdc) revert INVALID_SWAP_TOKENOUT();
        if (taoReceived == 0) revert INVALID_SWAP_AMOUNT();

        // Wrap TAO to WTAO
        IWTAO(wtao).deposit{value: taoReceived - bridgeParams.bridgeFee}();
        taoReceived -= bridgeParams.bridgeFee;
        userSwapAmount[msg.sender] = 0;

        // Approve and swap
        IERC20(wtao).approve(uniswapRouter, taoReceived);

        swapParams.amountIn = taoReceived;

        // Swap WTAO to USDC
        uint256 amountOut = IUniswapV3Router(uniswapRouter).exactInputSingle(swapParams);

        IERC20(usdc).approve(bridge, amountOut);

        IBridge(bridge).transferRemote{value: bridgeParams.bridgeFee}(
            bridgeParams.destinationChainId, bridgeParams.receiver, amountOut
        );
    }
}

contracts/v2/AlphaTokenMigrator.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.22;

import {IERC20} from "oz5/token/ERC20/IERC20.sol";
import {SafeERC20} from "oz5/token/ERC20/utils/SafeERC20.sol";
import {IBeaconProxyFactory} from "../interfaces/IBeaconProxyFactory.sol";

contract AlphaTokenMigrator {
    IBeaconProxyFactory public beaconProxyFactory;

    constructor(address _beaconProxyFactory) {
        beaconProxyFactory = IBeaconProxyFactory(_beaconProxyFactory);
    }

    function getNextAccountAddress(bytes32 salt) external view returns (address) {
        salt = keccak256(abi.encode(salt, msg.sender));
        return beaconProxyFactory.getNextProxyAddress(salt, address(this));
    }

    function createAccountAndMigrate(bytes32[] memory initialOwners, address[] memory alphaTokens, bytes32 salt)
        external
    {
        salt = keccak256(abi.encode(salt, msg.sender));
        address account = beaconProxyFactory.createProxy(initialOwners, salt);

        for (uint256 i = 0; i < alphaTokens.length; i++) {
            address token = alphaTokens[i];
            uint256 amount = IERC20(token).balanceOf(msg.sender);
            SafeERC20.safeTransferFrom(IERC20(token), msg.sender, account, amount);
        }
    }
}

contracts/v2/BeaconProxyFactory.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.22;

import {BeaconProxy} from "oz5/proxy/beacon/BeaconProxy.sol";
import {UpgradeableBeacon} from "oz5/proxy/beacon/UpgradeableBeacon.sol";
import {Create2} from "oz5/utils/Create2.sol";
import {IBeaconProxyFactory} from "../interfaces/IBeaconProxyFactory.sol";
import {IMultiOwnableSmartAccount} from "../interfaces/IMultiOwnableSmartAccount.sol";
import {MultiOwnableSmartAccount} from "./MultiOwnableSmartAccount.sol";

contract BeaconProxyFactory is UpgradeableBeacon, IBeaconProxyFactory {
    address public smartAccountRegistry;

    constructor(address _implementation, address _owner, address _smartAccountRegistry)
        UpgradeableBeacon(_implementation, _owner)
    {
        smartAccountRegistry = _smartAccountRegistry;
    }

    /// @inheritdoc IBeaconProxyFactory
    function getNextProxyAddress(bytes32 salt, address sender) external view returns (address proxy) {
        salt = keccak256(abi.encode(salt, sender));

        address beacon = address(this);
        bytes memory initialize = abi.encodeWithSelector(MultiOwnableSmartAccount.initialize.selector, beacon);
        bytes memory bytecode = abi.encodePacked(type(BeaconProxy).creationCode, abi.encode(beacon, initialize));

        return Create2.computeAddress(salt, keccak256(bytecode), address(this));
    }

    /// @inheritdoc IBeaconProxyFactory
    function createProxy(bytes32[] memory initialOwners, bytes32 salt) external returns (address proxy) {
        address beacon = address(this);

        bytes memory initialize = abi.encodeWithSelector(MultiOwnableSmartAccount.initialize.selector, beacon);
        bytes memory bytecode = abi.encodePacked(type(BeaconProxy).creationCode, abi.encode(beacon, initialize));

        salt = keccak256(abi.encode(salt, msg.sender));
        proxy = Create2.deploy(0, salt, bytecode);

        IMultiOwnableSmartAccount(proxy).setInitialOwners(initialOwners);

        emit ProxyCreated(proxy, initialOwners);
    }
}

contracts/v2/MultiOwnableSmartAccount.so.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.22;

import {Initializable} from "oz5/proxy/utils/Initializable.sol";
import {EnumerableSet} from "oz5/utils/structs/EnumerableSet.sol";
import {ECDSA} from "oz5/utils/cryptography/ECDSA.sol";
import {IMultiOwnableSmartAccount} from "../interfaces/IMultiOwnableSmartAccount.sol";
import {IBeaconProxyFactory} from "../interfaces/IBeaconProxyFactory.sol";
import {ISmartAccountRegistry} from "../interfaces/ISmartAccountRegistry.sol";

contract MultiOwnableSmartAccount is IMultiOwnableSmartAccount, Initializable {
    using EnumerableSet for EnumerableSet.Bytes32Set;
    using ECDSA for bytes32;

    bytes32 public constant EIP712_DOMAIN_TYPEHASH =
        keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)");

    bytes32 public constant NAME_HASH = keccak256("MultiOwnableSmartAccount");
    bytes32 public constant VERSION_HASH = keccak256("1");

    bytes32 public constant TYPEHASH_EXECUTE = keccak256(
        "ExecuteWithSig(uint8 namespace,bytes32 managerId,address target,uint256 value,bytes32 dataHash,uint256 nonce,uint256 deadline)"
    );
    bytes32 public constant TYPEHASH_BATCH_EXECUTE = keccak256(
        "BatchExecuteWithSig(uint8 namespace,bytes32 managerId,bytes32 targetsHash,bytes32 valuesHash,bytes32 payloadsHash,uint256 nonce,uint256 deadline)"
    );

    struct MultiOwnableSmartAccountStorage {
        /// @notice The address of the factory.
        address factory;
        /// @notice The set of owners.
        EnumerableSet.Bytes32Set owners;
        /// @notice The nonce for each owner.
        mapping(bytes32 => uint256) nonces;
    }

    function _getMultiOwnableSmartAccountStorage() internal pure returns (MultiOwnableSmartAccountStorage storage $) {
        bytes32 slot = keccak256(abi.encode(uint256(keccak256("taofi.contracts.storage.MultiOwnableSmartAccount")) - 1))
            & ~bytes32(uint256(0xff));
        assembly {
            $.slot := slot
        }
    }

    modifier onlySelf() {
        require(msg.sender == address(this), "MOSA:NOT_SELF");
        _;
    }

    modifier onlyOwner() {
        require(isOwner(_toOwnerId(msg.sender)), "MOSA:NOT_OWNER");
        _;
    }

    receive() external payable {}

    function initialize(address factory) external initializer {
        _getMultiOwnableSmartAccountStorage().factory = factory;
    }

    /// @inheritdoc IMultiOwnableSmartAccount
    function getFactory() public view returns (address) {
        return _getMultiOwnableSmartAccountStorage().factory;
    }

    /// @inheritdoc IMultiOwnableSmartAccount
    function getOwners() public view returns (bytes32[] memory) {
        return _getMultiOwnableSmartAccountStorage().owners.values();
    }

    /// @inheritdoc IMultiOwnableSmartAccount
    function isOwner(bytes32 ownerId) public view returns (bool) {
        return _getMultiOwnableSmartAccountStorage().owners.contains(ownerId);
    }

    /// @inheritdoc IMultiOwnableSmartAccount
    function getNonce(bytes32 ownerId) public view returns (uint256) {
        return _getMultiOwnableSmartAccountStorage().nonces[ownerId];
    }

    /// @inheritdoc IMultiOwnableSmartAccount
    function domainSeparatorV4() public view returns (bytes32) {
        return keccak256(abi.encode(EIP712_DOMAIN_TYPEHASH, NAME_HASH, VERSION_HASH, block.chainid, address(this)));
    }

    /// @inheritdoc IMultiOwnableSmartAccount
    function setInitialOwners(bytes32[] calldata initialOwnerIds) external {
        require(getOwners().length == 0, "MOSA:ALREADY_INITIALIZED");

        MultiOwnableSmartAccountStorage storage $ = _getMultiOwnableSmartAccountStorage();
        address smartAccountRegistry = IBeaconProxyFactory($.factory).smartAccountRegistry();

        for (uint256 i = 0; i < initialOwnerIds.length; i++) {
            $.owners.add(initialOwnerIds[i]);
            emit OwnerAdded(initialOwnerIds[i]);

            ISmartAccountRegistry(smartAccountRegistry).registerSmartAccount(initialOwnerIds[i]);
        }
    }

    /// @inheritdoc IMultiOwnableSmartAccount
    function addOwner(bytes32 ownerId) external onlySelf {
        require(!isOwner(ownerId), "MOSA:ALREADY_OWNER");

        _getMultiOwnableSmartAccountStorage().owners.add(ownerId);

        address smartAccountRegistry = IBeaconProxyFactory(getFactory()).smartAccountRegistry();
        ISmartAccountRegistry(smartAccountRegistry).registerSmartAccount(ownerId);

        emit OwnerAdded(ownerId);
    }

    /// @inheritdoc IMultiOwnableSmartAccount
    function removeOwner(bytes32 ownerId) external onlySelf {
        require(isOwner(ownerId), "MOSA:NOT_OWNER");

        address smartAccountRegistry = IBeaconProxyFactory(getFactory()).smartAccountRegistry();
        ISmartAccountRegistry(smartAccountRegistry).unregisterSmartAccount(ownerId);

        _getMultiOwnableSmartAccountStorage().owners.remove(ownerId);

        emit OwnerRemoved(ownerId);
    }

    /// @inheritdoc IMultiOwnableSmartAccount
    function executeCall(address target, uint256 value, bytes memory data) external payable onlyOwner {
        _execute(target, value, data);
    }

    /// @inheritdoc IMultiOwnableSmartAccount
    function executeCalls(address[] memory targets, uint256[] memory values, bytes[] memory data)
        external
        payable
        onlyOwner
    {
        for (uint256 i = 0; i < targets.length; i++) {
            _execute(targets[i], values[i], data[i]);
        }
    }

    /// @inheritdoc IMultiOwnableSmartAccount
    function executeWithSig(
        uint8 namespace,
        bytes32 managerId,
        address target,
        uint256 value,
        bytes calldata data,
        uint256 nonce,
        uint256 deadline,
        bytes calldata signature
    ) external payable {
        address expectedSigner = address(uint160(uint256(managerId)));

        require(namespace == 1, "MOSA:NS_UNSUPPORTED");
        require(block.timestamp <= deadline, "MOSA:EXPIRED");
        require(isOwner(managerId), "MOSA:NOT_OWNER");

        MultiOwnableSmartAccountStorage storage $ = _getMultiOwnableSmartAccountStorage();
        require(nonce == $.nonces[managerId], "MOSA:BAD_NONCE");

        bytes32 structHash = keccak256(
            abi.encode(TYPEHASH_EXECUTE, namespace, managerId, target, value, keccak256(data), nonce, deadline)
        );

        bytes32 digest = keccak256(abi.encodePacked("\x19\x01", domainSeparatorV4(), structHash));
        address signer = ECDSA.recover(digest, signature);

        require(signer == expectedSigner, "MOSA:BAD_SIG");

        $.nonces[managerId]++;
        _execute(target, value, data);
    }

    /// @inheritdoc IMultiOwnableSmartAccount
    function batchExecuteWithSig(
        uint8 namespace,
        bytes32 managerId,
        address[] calldata targets,
        uint256[] calldata values,
        bytes[] calldata payloads,
        uint256 nonce,
        uint256 deadline,
        bytes calldata signature
    ) external payable {
        address expectedSigner = address(uint160(uint256(managerId)));

        require(namespace == 1, "MOSA:NS_UNSUPPORTED");
        require(targets.length == values.length && targets.length == payloads.length, "MOSA:LENGTH_MISMATCH");
        require(block.timestamp <= deadline, "MOSA:EXPIRED");
        require(isOwner(managerId), "MOSA:NOT_OWNER");
        require(nonce == getNonce(managerId), "MOSA:BAD_NONCE");

        {
            bytes32 structHash = keccak256(
                abi.encode(
                    TYPEHASH_BATCH_EXECUTE,
                    namespace,
                    managerId,
                    keccak256(abi.encodePacked(targets)),
                    keccak256(abi.encodePacked(values)),
                    _hashBytesArray(payloads),
                    nonce,
                    deadline
                )
            );

            bytes32 digest = keccak256(abi.encodePacked("\x19\x01", domainSeparatorV4(), structHash));
            address signer = ECDSA.recover(digest, signature);

            require(signer == expectedSigner, "MOSA:BAD_SIG");
        }

        _getMultiOwnableSmartAccountStorage().nonces[managerId]++;

        for (uint256 i = 0; i < targets.length; i++) {
            _execute(targets[i], values[i], payloads[i]);
        }
    }

    /// @dev Executes a single call.
    /// @param target The address of the target contract.
    /// @param value The value to send with the call.
    /// @param data The data to send with the call.
    function _execute(address target, uint256 value, bytes memory data) internal {
        (bool success, bytes memory ret) = target.call{value: value}(data);

        if (!success) {
            assembly {
                revert(add(ret, 0x20), mload(ret))
            }
        }
    }

    /// @dev Hashes an array of bytes.
    /// @param payloads The array of bytes to hash.
    /// @return The hash of the array of bytes.
    function _hashBytesArray(bytes[] calldata payloads) internal pure returns (bytes32) {
        bytes32[] memory hashes = new bytes32[](payloads.length);

        for (uint256 i = 0; i < payloads.length; i++) {
            hashes[i] = keccak256(payloads[i]);
        }

        return keccak256(abi.encodePacked(hashes));
    }

    /// @dev Converts an EVM address to the canonical bytes32 ownerId (left-padded).
    function _toOwnerId(address account) internal pure returns (bytes32) {
        return bytes32(uint256(uint160(account)));
    }
}

contracts/v2/SmartAccountRegistry.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.22;

import {EnumerableSet} from "oz5/utils/structs/EnumerableSet.sol";

import {ISmartAccountRegistry} from "../interfaces/ISmartAccountRegistry.sol";
import {IMultiOwnableSmartAccount} from "../interfaces/IMultiOwnableSmartAccount.sol";

contract SmartAccountRegistry is ISmartAccountRegistry {
    using EnumerableSet for EnumerableSet.AddressSet;

    mapping(bytes32 owner => EnumerableSet.AddressSet) smartAccounts;

    /// @inheritdoc ISmartAccountRegistry
    function getSmartAccounts(bytes32 owner) external view returns (address[] memory) {
        return smartAccounts[owner].values();
    }

    /// @inheritdoc ISmartAccountRegistry
    function registerSmartAccount(bytes32 owner) external {
        require(IMultiOwnableSmartAccount(msg.sender).isOwner(owner), "SAR: NOT_OWNER");

        smartAccounts[owner].add(msg.sender);

        emit SmartAccountRegistered(owner, msg.sender);
    }

    /// @inheritdoc ISmartAccountRegistry
    function unregisterSmartAccount(bytes32 owner) external {
        require(IMultiOwnableSmartAccount(msg.sender).isOwner(owner), "SAR: NOT_OWNER");

        smartAccounts[owner].remove(msg.sender);

        emit SmartAccountUnregistered(owner, msg.sender);
    }
}

